CREATE TABLE `lz_history` (
  `id` int(11) NOT NULL,
  `time_year` int(11) NOT NULL DEFAULT '0',
  `img` varchar(255) DEFAULT NULL,
  `content` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Company history';

--
-- 转存表中的数据 `lz_history`
--

INSERT INTO `lz_history` (`id`, `time_year`, `img`, `content`) VALUES
(1, 2008, NULL, '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;	第一本《业主手册》(香樟雅郡)问世，首创中国“新楼盘终端媒体”模式。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>'),
(2, 2009, NULL, '1月13日，注册成立长沙卓远文化传播有限公司。与同升湖、保利阆峰、碧桂园威尼斯城等物业成功合作。'),
(3, 2010, NULL, '获长沙市雨花区创业资金扶持，在长沙与绿城、保利、碧桂园、长城等大型物业合作，服务本土上百家一二线、家居建材商户品牌。'),
(4, 2011, NULL, '签下第一家城市代理商——合肥，开始了布局全国传媒蓝图的征程。'),
(5, 2012, NULL, '全国代理城市达60家，卓远品牌在全国市场的知名度、美誉度同步提升。曹志远先生获“中国创业明星”、长沙市“创业新星”和“金牌创业导师”等荣誉，被各主流媒体广泛报道。'),
(6, 2013, NULL, '自购华雅国际大酒店一号公馆12层独立办公室,跨入现代化、品牌化、标准化企业发展新里程。曹志远接收央视CCTV13新闻频道《面对面》和CCTV7军事农业频道《致富经》等栏目专访,被誉为“85后创业年轻人典范”。'),
(7, 2014, NULL, '启动乐住智慧小区项目，自主研发的智慧物业平台投入使用，“乐住”App第一版上线。12月，与长沙市民政局、雨花区人民政府联合举办了乐住智慧社区项目试点新闻发布会。'),
(8, 2015, NULL, '2月，公司与中国银联（湖南分公司）签署战略合作。8月，公司正式更名为“乐住网络服务有限公司”，与500多个小区合作开展智慧小区建设。11月，启动全国城市运营合作计划，长春、岳阳等城市已开展运营。12月16日，在长沙人民会堂举办，央视名嘴董倩主持的“互联网+小区”项目启动仪式暨新闻发布会。'),
(9, 2016, NULL, '3月，打造长沙四大样板小区，成为全国城市运营的示范标杆实例。6月，乐住网络创始人曹志远受到人民网专题报道。7月，乐住智慧小区获得国家住建委《中国建设报》深度报道与推介。'),
(10, 2017, NULL, '5月 ，乐住智慧小区被湖南卫视、芒果TV等10余家主流媒体报道与推介。'),
(11, 2008, NULL, 'content'),
(12, 2008, NULL, '<p>2</p>');

